import avaliacao from './assets/avaliacao.png'
import grafico from './assets/grafico.png'
import consulta from './assets/consulta.png'
import styled from 'styled-components'

interface Props {
    imagem?: string;
    children?: React.ReactNode;
}

interface IImagens {
    avaliacao?: string;
    grafico?: string;
    consulta?: string;
}

const SpanEstilizado = styled.span<Props>`
    background-repeat: no-repeat;
    background-position: center;
    background-size: conver;
    widht: 25px;
    height: 25px;
    background-image: ${props => props.imagem ? `url(${props.imagem})`: 'none'};
`

const TituloEstilizado = styled.h2`
color: var(--azul-claro);
`

const ContainerEstilizado = styled.div`
    display: flex;
    align-items: center;
`

function Título({imagem, children}: Props) {

    const listaImagens: IImagens = {
        avaliacao: avaliacao,
        grafico: grafico,
        consulta: consulta
    }
    return (
        <ContainerEstilizado>
            <SpanEstilizado imagem={listaImagens[imagem as keyof IImagens]}/>
            <TituloEstilizado>{children}</TituloEstilizado> 
        </ContainerEstilizado>

    )
}

export default Título;
